mapper.select:
SELECT COUNT(*) FROM Board
WHERE


		OR title LIKE #{keyword}



		OR content LIKE #{keyword}



		OR writer LIKE #{keyword}

SELECT b.id, b.title, b.writer, b.inserted, COUNT(DISTINCT r.id) countReply, COUNT(DISTINCT f.id) countFile FROM Board b LEFT JOIN Reply r ON b.id = r.boardId LEFT JOIN File f ON b.id = f.boardId WHERE
OR b.title LIKE #{keyword}
OR b.content LIKE #{keyword}
OR b.writer LIKE #{keyword}
GROUP BY b.id ORDER BY b.id DESC LIMIT #{offset}, #{records}
